package org.esa.beam.extapi.gen;

import java.io.File;
import java.io.FileWriter;
import java.io.IOException;
import java.io.PrintWriter;

/**
 * @author Norman Fomferra
 */
public abstract class TargetCFile extends TargetFile{

    public TargetCFile(String dirPath, String fileName) throws IOException {
        super(dirPath, fileName);
    }

    protected void writeHeader() throws IOException {
        writer.write(String.format("/*\n" +
                                           " * DO NOT EDIT THIS FILE, IT IS MACHINE-GENERATED\n" +
                                           " * This code has been generated using %s\n" +
                                           " */\n", ApiGeneratorDoclet.class.getName()));
        writer.write("\n");
    }

    protected void writeFooter() throws IOException {
    }
}
